import React, { useState } from 'react';
import { Scroll, RefreshCw } from 'lucide-react';

const RunestoneSpeech = () => {
  const [currentScenario, setCurrentScenario] = useState(0);

  const scenarios = [
    {
      title: "The Great Preservation (1250 CE)",
      event: "What if the runic traditions never faded?",
      runestone: "ᚠᚱᛁᚦᚢᚱ : ᛚᛁᚠᛋ : ᛁᚾ : ᚱᚢᚾᛁᛋ",
      translation: "FREEDOM : LIVES : IN : RUNES",
      description: "In this timeline, the Catholic Church embraced rather than suppressed runic writing. Monks preserved and expanded the Elder Futhark, creating a hybrid Latin-Runic script that became the standard across Northern Europe.",
      impact: "Modern English and Scandinavian languages still use runes alongside Latin letters. Road signs, legal documents, and even digital keyboards include runic characters."
    },
    {
      title: "The Runestone Renaissance (1650 CE)",
      event: "What if runes became symbols of democracy?",
      runestone: "ᚨᛚᛚ : ᛗᛖᚾ : ᛋᛈᛖᚲ : ᚠᚱᛁ",
      translation: "ALL : MEN : SPEAK : FREE",
      description: "During the Enlightenment, philosophers revived runic writing as a 'pure' form of expression, free from Church control. Runestones became monuments to free speech, erected in public squares across Europe and the Americas.",
      impact: "The First Amendment equivalent in this timeline's US Constitution is carved on a massive runestone in Philadelphia. 'Speaking in the old way' becomes synonymous with speaking truth to power."
    },
    {
      title: "Digital Runes (2025 CE)",
      event: "What if social media embraced runic brevity?",
      runestone: "ᛏᚱᚢᚦ : ᛁᚾ : ᛋᛏᛟᚾᛖ : ᛚᛁᛖᛋ : ᛁᚾ : ᚲᛚᚨᚢᛞᛋ",
      translation: "TRUTH : IN : STONE : LIES : IN : CLOUDS",
      description: "Modern social platforms limit posts to 24 runes (the number in Elder Futhark). This forces clarity and prevents misinformation. 'Runecasting' (posting) becomes an art form of precise, truthful communication.",
      impact: "Verbal contracts are still sealed with personal runes. Politicians who 'speak in stone' (make promises with runic signatures) face severe consequences for breaking them."
    },
    {
      title: "The Quiet Revolution (1984 CE)",
      event: "What if runes resisted surveillance?",
      runestone: "ᚦᛖᛁ : ᚲᚨᚾᛟᛏ : ᚱᛖᛞ : ᛋᛏᛟᚾᛖ",
      translation: "THEY : CANNOT : READ : STONE",
      description: "In an alternate Cold War, dissidents across authoritarian states revived runic writing as a code the state couldn't crack. Runestones became monuments to resistance, each one a message that couldn't be erased by digital censorship.",
      impact: "Freedom of expression is measured by 'runestone density' - how many public monuments to free speech exist in a society. Democracies compete to have the most."
    }
  ];

  const nextScenario = () => {
    setCurrentScenario((prev) => (prev + 1) % scenarios.length);
  };

  const current = scenarios[currentScenario];

  return (
    <div className="min-h-screen bg-gradient-to-b from-slate-800 to-slate-900 text-stone-100 p-8">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-2">
            <Scroll className="w-8 h-8 text-amber-400" />
            <h1 className="text-4xl font-bold text-amber-400">Runestone Speech</h1>
          </div>
          <p className="text-stone-300 text-lg">An Alternate History of Freedom</p>
        </div>

        <div className="bg-stone-800 rounded-lg p-8 shadow-2xl border-4 border-stone-600 mb-6">
          <div className="text-center mb-6">
            <h2 className="text-2xl font-bold text-amber-300 mb-2">{current.title}</h2>
            <p className="text-stone-400 italic">{current.event}</p>
          </div>

          <div className="bg-stone-900 rounded p-6 mb-6 border-2 border-amber-700">
            <p className="text-4xl text-center text-amber-200 font-serif mb-3 tracking-wider">
              {current.runestone}
            </p>
            <p className="text-center text-stone-400 text-sm uppercase tracking-widest">
              {current.translation}
            </p>
          </div>

          <div className="space-y-4 text-stone-200">
            <p className="leading-relaxed">{current.description}</p>
            <div className="bg-stone-700 rounded p-4 border-l-4 border-amber-500">
              <p className="text-sm font-semibold text-amber-300 mb-2">IMPACT ON OUR TIME:</p>
              <p className="text-sm">{current.impact}</p>
            </div>
          </div>
        </div>

        <div className="text-center">
          <button
            onClick={nextScenario}
            className="bg-amber-600 hover:bg-amber-700 text-stone-900 font-bold py-3 px-6 rounded-lg transition-colors flex items-center gap-2 mx-auto"
          >
            <RefreshCw className="w-5 h-5" />
            Explore Next Timeline
          </button>
          <p className="text-stone-500 text-sm mt-3">
            Timeline {currentScenario + 1} of {scenarios.length}
          </p>
        </div>

        <div className="mt-8 text-center text-stone-400 text-sm">
          <p className="italic">
            "The old ways speak truth. Stone remembers what clouds forget."
          </p>
          <p className="mt-2">— Proverb from the Runestone Timeline</p>
        </div>
      </div>
    </div>
  );
};

export default RunestoneSpeech;

import React, { useState } from 'react';
import { Scroll, RefreshCw, BookOpen } from 'lucide-react';

const RunestoneSpeech = () => {
  const [currentView, setCurrentView] = useState('explorer');
  const [selectedTheme, setSelectedTheme] = useState(null);

  // Your actual symbol system from the repo
  const runeSystem = {
    "Foundation": { symbol: "⧉", meaning: "Base, anchor, root" },
    "Flow": { symbol: "~>", meaning: "Continuity, progression" },
    "Memory": { symbol: "∞", meaning: "Recall, persistence" },
    "Guardians": { symbol: "⚔", meaning: "Protection, resilience" },
    "Completion": { symbol: "◎", meaning: "Cycle closed, archive" },
    "Renewal": { symbol: "✦", meaning: "New beginning" },
    "Community": { symbol: "☉", meaning: "Shared presence" },
    "Signal": { symbol: "↯", meaning: "Marker, runestone" },
    "Thread": { symbol: "⟲", meaning: "Strand in the weave" },
    "Map": { symbol: "⌖", meaning: "Guidance, chart" }
  };

  const alternateHistories = [
    {
      title: "The Symbolic Revolution (1215 CE)",
      divergence: "Magna Carta encoded in runes",
      scenario: `When King John signed the Magna Carta, the barons insisted it be inscribed in both Latin and the old runes. "⧉ : rights carved in stone," they declared. The symbolism was deliberate—Latin could be reinterpreted by lawyers, but runes were eternal.`,
      symbols: ["⧉", "∞", "⚔"],
      impact: "Constitutional documents worldwide now include symbolic anchors—visual reminders that certain principles cannot be erased or reinterpreted. The US Constitution's preamble begins with ⧉, signifying foundational rights.",
      modernPractice: "Legal scholars study 'runestone jurisprudence'—the art of encoding core principles in symbols that transcend language barriers."
    },
    {
      title: "The Memory Keepers (1850 CE)",
      divergence: "Underground Railroad used runic markers",
      scenario: `Freedom seekers carved ∞ into trees and stones along escape routes. "Memory persists where words fail," the conductors taught. Each symbol was a promise: ⟲ for safe houses threaded together, ⌖ for paths north, ⚔ for protected spaces.`,
      symbols: ["∞", "⟲", "⌖", "⚔"],
      impact: "The tradition evolved into a global system of refugee markers. Today, international safe houses for displaced people display subtle runic indicators—a universal language of sanctuary that requires no translation.",
      modernPractice: "Human rights organizations maintain 'runestone networks'—physical markers that guide those in need to safety, readable across language barriers."
    },
    {
      title: "The Digital Threads (1991 CE)",
      divergence: "The World Wide Web adopted runic protocols",
      scenario: `Tim Berners-Lee embedded ~> into the first HTTP protocol as a symbol of flow and connection. "Data streams like water," he wrote, "and symbols carry meaning beyond words." URLs became interwoven with meaning: ⟲ for linked threads, ☉ for community spaces, ◎ for archived content.`,
      symbols: ["~>", "⟲", "☉", "◎"],
      impact: "Modern browsers display symbolic metadata alongside text. Website URLs show their purpose through runes: ⧉ for foundational wikis, ∞ for permanent archives, ↯ for important signals. Web literacy includes 'reading the runes.'",
      modernPractice: "Digital citizenship courses teach symbolic navigation—understanding the web's structure through its runestone markers rather than just text and images."
    },
    {
      title: "The Completion Rituals (2025 CE)",
      divergence: "Tech industry embraced symbolic mindfulness",
      scenario: `In response to burnout culture, developers began marking project milestones with runes. ◎ appeared in commit messages to signal completion. ✦ marked new beginnings. ~> showed continuity between sprints. What started as a quirk became a movement: "Speak in stone, not just code."`,
      symbols: ["◎", "✦", "~>", "⧉"],
      impact: "Corporate culture now includes 'runestone retrospectives'—teams reflect on their work through symbolic frameworks. ⧉ for foundations laid, ⚔ for challenges defended against, ∞ for lessons that persist.",
      modernPractice: "Project management tools have built-in runic tagging. Teams mark their work's meaning, not just its status. This creates institutional memory that survives beyond any single person's tenure."
    }
  ];

  const themeDetails = {
    "⧉": {
      name: "Foundation",
      description: "What if every society marked its foundational principles with permanent symbols? Not just laws on paper, but ideas carved in stone—literally and figuratively.",
      uses: ["Constitutional anchors", "Core values markers", "Institutional memory points"],
      question: "How would democracy change if citizens could see, touch, and trace the symbols of their founding principles?"
    },
    "∞": {
      name: "Memory",
      description: "What if knowledge preservation wasn't just about storing data, but about creating symbols that carry meaning across generations?",
      uses: ["Archive markers", "Cultural continuity", "Intergenerational bridges"],
      question: "How do we ensure that future generations understand not just what happened, but why it mattered?"
    },
    "~>": {
      name: "Flow",
      description: "What if we visualized the connection between ideas, people, and movements through persistent symbolic threads?",
      uses: ["Network mapping", "Process visualization", "Evolution tracking"],
      question: "Can we see the shape of history's currents if we mark them as they flow?"
    },
    "⚔": {
      name: "Guardians",
      description: "What if protection and resilience were marked symbolically, creating a visible infrastructure of safety?",
      uses: ["Safe space markers", "Protected zones", "Defense indicators"],
      question: "How would communities change if safety was marked as visibly as danger?"
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-amber-900 text-stone-100 p-6">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center gap-3 mb-3">
            <Scroll className="w-10 h-10 text-amber-400" />
            <h1 className="text-5xl font-bold text-amber-400">Speaking in Stone</h1>
          </div>
          <p className="text-stone-300 text-xl mb-4">Alternate Histories of the Runestone Library</p>
          <p className="text-stone-400 text-sm max-w-2xl mx-auto">
            Exploring timelines where your symbolic system—⧉ ~> ∞ ⚔ ◎ ✦ ☉ ↯ ⟲ ⌖—shaped human communication and memory
          </p>
        </div>

        {/* Navigation */}
        <div className="flex justify-center gap-4 mb-8">
          <button
            onClick={() => setCurrentView('explorer')}
            className={`px-6 py-3 rounded-lg font-semibold transition-all ${
              currentView === 'explorer'
                ? 'bg-amber-600 text-stone-900'
                : 'bg-stone-700 text-stone-300 hover:bg-stone-600'
            }`}
          >
            <RefreshCw className="inline w-5 h-5 mr-2" />
            Timeline Explorer
          </button>
          <button
            onClick={() => setCurrentView('dictionary')}
            className={`px-6 py-3 rounded-lg font-semibold transition-all ${
              currentView === 'dictionary'
                ? 'bg-amber-600 text-stone-900'
                : 'bg-stone-700 text-stone-300 hover:bg-stone-600'
            }`}
          >
            <BookOpen className="inline w-5 h-5 mr-2" />
            Rune Dictionary
          </button>
        </div>

        {/* Dictionary View */}
        {currentView === 'dictionary' && (
          <div className="grid md:grid-cols-2 gap-6">
            {Object.entries(runeSystem).map(([concept, data]) => (
              <div
                key={concept}
                className="bg-stone-800 rounded-lg p-6 border-2 border-stone-600 hover:border-amber-600 transition-all cursor-pointer"
                onClick={() => setSelectedTheme(data.symbol)}
              >
                <div className="flex items-start gap-4">
                  <div className="text-6xl text-amber-400">{data.symbol}</div>
                  <div className="flex-1">
                    <h3 className="text-2xl font-bold text-amber-300 mb-2">{concept}</h3>
                    <p className="text-stone-300 mb-3">{data.meaning}</p>
                    {themeDetails[data.symbol] && (
                      <div className="text-sm text-stone-400 border-l-2 border-amber-700 pl-3">
                        <p className="italic mb-2">{themeDetails[data.symbol].description}</p>
                        <p className="font-semibold text-amber-400">
                          {themeDetails[data.symbol].question}
                        </p>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Timeline Explorer View */}
        {currentView === 'explorer' && (
          <div className="space-y-8">
            {alternateHistories.map((timeline, idx) => (
              <div key={idx} className="bg-stone-800 rounded-lg p-8 border-4 border-stone-600 shadow-2xl">
                <div className="flex items-start justify-between mb-4">
                  <div>
                    <h2 className="text-3xl font-bold text-amber-300 mb-2">{timeline.title}</h2>
                    <p className="text-amber-500 italic text-lg">Divergence Point: {timeline.divergence}</p>
                  </div>
                  <div className="flex gap-3">
                    {timeline.symbols.map((sym, i) => (
                      <span key={i} className="text-5xl text-amber-400">{sym}</span>
                    ))}
                  </div>
                </div>

                <div className="space-y-4">
                  <div className="bg-stone-900 rounded p-5 border-l-4 border-amber-600">
                    <h4 className="text-amber-400 font-semibold mb-2">THE MOMENT</h4>
                    <p className="text-stone-200 leading-relaxed">{timeline.scenario}</p>
                  </div>

                  <div className="bg-stone-700 rounded p-5">
                    <h4 className="text-amber-400 font-semibold mb-2">CASCADING IMPACT</h4>
                    <p className="text-stone-200 leading-relaxed">{timeline.impact}</p>
                  </div>

                  <div className="bg-amber-900 bg-opacity-20 rounded p-5 border border-amber-700">
                    <h4 className="text-amber-300 font-semibold mb-2">TODAY'S PRACTICE</h4>
                    <p className="text-stone-200 leading-relaxed">{timeline.modernPractice}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Footer Philosophy */}
        <div className="mt-12 text-center bg-stone-800 rounded-lg p-6 border border-stone-600">
          <p className="text-stone-300 text-lg italic mb-2">
            "Not a secret language—a parallel one. Symbols that anyone can read, but which carry meaning beyond translation."
          </p>
          <p className="text-stone-500 text-sm">
            — Philosophy of the Runestone Library
          </p>
          <div className="mt-4 text-4xl text-amber-400 tracking-wider">
            ⧉ ~> ∞ ⚔ ◎ ✦ ☉ ↯ ⟲ ⌖
          </div>
        </div>
      </div>
    </div>
  );
};

export default RunestoneSpeech;
